import React, { useState, useMemo } from "react";
import { products as mockProducts } from "../mock/products";
import ProductCard from "../components/ProductCard";
import SearchBar from "../components/SearchBar";
import FilterBar from "../components/FilterBar";
import SortSelector from "../components/SortSelector";
import PartnerWidget from "../components/PartnerWidget";
import Footer from "../components/Footer";

// Google Analytics Ïù¥Î≤§Ìä∏ Ï∂îÏ†Å Ìï®Ïàò
declare global {
  interface Window {
    gtag: (...args: any[]) => void;
  }
}

const trackEvent = (eventName: string, parameters?: any) => {
  if (typeof window !== 'undefined' && window.gtag) {
    window.gtag('event', eventName, parameters);
  }
};

export default function Home() {
  const [keyword, setKeyword] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("Ï†ÑÏ≤¥");
  const [selectedCategory, setSelectedCategory] = useState("Ï†ÑÏ≤¥");
  const [sortBy, setSortBy] = useState("popular");

  // ÌïÑÌÑ∞ÎßÅÎêú ÏÉÅÌíàÎì§
  const filteredProducts = useMemo(() => {
    let filtered = mockProducts.filter((product) => {
      // Í≤ÄÏÉâÏñ¥ ÌïÑÌÑ∞
      const searchTarget = [
        product.name,
        product.description,
        ...product.tags,
        ...product.categories,
        ...product.locations,
      ].join(" ").toLowerCase();

      const keywordMatch = keyword === "" || searchTarget.includes(keyword.toLowerCase());

      // ÏßÄÏó≠ ÌïÑÌÑ∞
      const locationMatch = selectedLocation === "Ï†ÑÏ≤¥" || 
        product.locations.some(location => location.includes(selectedLocation));

      // Ïπ¥ÌÖåÍ≥†Î¶¨ ÌïÑÌÑ∞
      const categoryMatch = selectedCategory === "Ï†ÑÏ≤¥" || 
        product.categories.includes(selectedCategory);

      return keywordMatch && locationMatch && categoryMatch;
    });

    // Ï†ïÎ†¨
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "popular":
          return b.views - a.views;
        case "latest":
          // ID ÏàúÏÑúÎ°ú Ï†ïÎ†¨ (ÏµúÏã† Îì±Î°ù Ïàú)
          return parseInt(b.id.split('_')[1]) - parseInt(a.id.split('_')[1]);
        default:
          return 0;
      }
    });

    return filtered;
  }, [keyword, selectedLocation, selectedCategory, sortBy]);

  // Ï∂îÏ≤ú ÏÉÅÌíàÎì§
  const recommendedProducts = useMemo(() => {
    return filteredProducts.filter(product => product.isRecommended);
  }, [filteredProducts]);

  // ÏùºÎ∞ò ÏÉÅÌíàÎì§
  const regularProducts = useMemo(() => {
    return filteredProducts.filter(product => !product.isRecommended);
  }, [filteredProducts]);

  const handleProductClick = (productId: string) => {
    // Google Analytics Ïù¥Î≤§Ìä∏ Ï∂îÏ†Å
    trackEvent('product_click', {
      product_id: productId,
      event_category: 'engagement',
      event_label: 'product_card_click'
    });

    // Ïã§Ï†úÎ°úÎäî ÎùºÏö∞ÌÑ∞Î•º ÏÇ¨Ïö©ÌïòÏó¨ ÏÉÅÌíà ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    console.log(`ÏÉÅÌíà ${productId} ÌÅ¥Î¶≠Îê®`);
    // window.location.href = `/product/${productId}`;
  };

  const handleSearch = (searchTerm: string) => {
    if (searchTerm.trim()) {
      trackEvent('search', {
        search_term: searchTerm,
        event_category: 'search',
        event_label: 'keyword_search'
      });
    }
  };

  const handleFilterChange = (filterType: string, filterValue: string) => {
    trackEvent('filter_change', {
      filter_type: filterType,
      filter_value: filterValue,
      event_category: 'engagement',
      event_label: 'filter_interaction'
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-cyan-50">
      <div className="max-w-screen-md mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Ìó§Îçî */}
        <div className="text-center mb-6">
          <h1 className="text-2xl md:text-3xl font-bold bg-gradient-to-r from-blue-600 to-cyan-600 bg-clip-text text-transparent mb-2" style={{ fontFamily: 'Poppins, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
            TourStream
          </h1>
          <p className="text-gray-600 text-sm">
            ÏµúÍ≥†Ïùò Ìà¨Ïñ¥ ÏÉÅÌíàÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
          </p>
        </div>

            {/* Í≤ÄÏÉâÎ∞î */}
            <div className="mb-4">
              <SearchBar 
                value={keyword} 
                onChange={(value) => {
                  setKeyword(value);
                  handleSearch(value);
                }} 
              />
            </div>

        {/* ÌïÑÌÑ∞ Î∞è Ï†ïÎ†¨ */}
        <div className="mb-4">
          <FilterBar
            selectedLocation={selectedLocation}
            selectedCategory={selectedCategory}
            onLocationChange={setSelectedLocation}
            onCategoryChange={setSelectedCategory}
          />
          <SortSelector sortBy={sortBy} onSortChange={setSortBy} />
        </div>

        {/* Ï∂îÏ≤ú ÏÉÅÌíà ÏÑπÏÖò */}
        {recommendedProducts.length > 0 && (
          <div className="mb-6">
            <h2 className="text-xl font-bold text-gray-900 mb-3">üî• Ï∂îÏ≤ú ÏÉÅÌíà</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 mb-4">
              {recommendedProducts.map((product) => (
                <ProductCard 
                  key={product.id} 
                  product={product} 
                  onProductClick={handleProductClick}
                />
              ))}
            </div>
          </div>
        )}

        {/* ÏùºÎ∞ò ÏÉÅÌíà Î¶¨Ïä§Ìä∏ */}
        <div className="mb-6">
          <h2 className="text-xl font-bold text-gray-900 mb-3">
            {filteredProducts.length > 0 ? "Î™®Îì† ÏÉÅÌíà" : "Í≤ÄÏÉâ Í≤∞Í≥º"}
          </h2>
          {regularProducts.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {regularProducts.map((product) => (
                <ProductCard 
                  key={product.id} 
                  product={product} 
                  onProductClick={handleProductClick}
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <div className="text-gray-400 text-base mb-2">üîç</div>
              <p className="text-gray-500">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</p>
              <p className="text-gray-400 text-sm">Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî</p>
            </div>
          )}
        </div>

        {/* Ï†úÌú¥ÏÇ¨ */}
        <PartnerWidget />
      </div>

      <Footer />
    </div>
  );
}
