import React, { useState, memo } from "react";

interface ProductCardProps {
  product: any;
  onProductClick?: (productId: string) => void;
}

const ProductCard: React.FC<ProductCardProps> = ({ product, onProductClick }) => {
  const [isLiked, setIsLiked] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);

  const handleCardClick = () => {
    if (onProductClick) {
      onProductClick(product.id);
    }
  };

  const handleLikeClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsLiked(!isLiked);
  };

  const handleExpandClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsExpanded(!isExpanded);
  };

  // 구조화된 데이터 생성
  const structuredData = {
    "@context": "https://schema.org",
    "@type": "Product",
    "name": product.name,
    "description": product.description,
    "category": product.categories.join(", "),
    "brand": {
      "@type": "Brand",
      "name": "TourStream"
    },
    "offers": {
      "@type": "Offer",
      "availability": product.isAvailable ? "https://schema.org/InStock" : "https://schema.org/OutOfStock",
      "seller": {
        "@type": "Organization",
        "name": "TourStream"
      }
    },
    "locationCreated": {
      "@type": "Place",
      "name": product.locations.join(", "),
      "address": {
        "@type": "PostalAddress",
        "addressCountry": "KR"
      }
    },
    "keywords": product.tags.join(", ")
  };

  return (
    <>
      {/* 구조화된 데이터 */}
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{
          __html: JSON.stringify(structuredData)
        }}
      />

      <div className="bg-white rounded-2xl shadow-sm hover:shadow-lg transition-all duration-300 overflow-hidden border border-gray-100 group cursor-pointer" onClick={handleCardClick}>
        {/* 상품 정보 */}
        <div className="p-6">
          {/* 카테고리 태그 */}
          <div className="flex gap-2 mb-3">
            {product.categories.slice(0, 2).map((category: string, idx: number) => (
              <span key={idx} className="text-xs bg-gradient-to-r from-blue-100 to-cyan-100 text-blue-700 px-3 py-1 rounded-full font-medium">
                {category}
              </span>
            ))}
          </div>

          {/* 상품명 */}
          <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors">
            {product.name}
          </h3>

          {/* 설명 */}
          <p className="text-sm text-gray-600 mb-4 line-clamp-2 leading-relaxed">
            {product.description}
          </p>

          {/* 위치 정보 */}
          <div className="flex items-center gap-2 mb-4">
            <svg className="h-4 w-4 text-gray-400 flex-shrink-0" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            <span className="text-sm text-gray-500">{product.locations.join(", ")}</span>
          </div>

          {/* 태그 */}
          <div className="flex flex-wrap gap-1 mb-4">
            {product.tags.slice(0, 3).map((tag: string, idx: number) => (
              <span key={idx} className="text-xs text-blue-600 bg-blue-50 px-2 py-1 rounded-lg">
                #{tag}
              </span>
            ))}
            {product.tags.length > 3 && (
              <span className="text-xs text-gray-400 px-2 py-1">
                +{product.tags.length - 3}개
              </span>
            )}
          </div>
        </div>

        {/* 예약 링크 섹션 */}
        {product.externalUrls && product.externalUrls.filter((url: string) => url && url.trim() !== '').length > 0 && (
          <div className="border-t border-gray-100 bg-gray-50/50">
            <button
              onClick={handleExpandClick}
              className="flex items-center justify-between w-full p-4 text-left hover:bg-gray-100 transition-colors"
            >
              <div className="flex items-center gap-3">
                <div className="w-8 h-8 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-full flex items-center justify-center">
                  <svg className="h-4 w-4 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                  </svg>
                </div>
                <div>
                  <span className="font-medium text-gray-900">
                    예약 링크 {product.externalUrls.filter((url: string) => url && url.trim() !== '').length}개
                  </span>
                  <p className="text-xs text-gray-500">클릭하여 예약 사이트 확인</p>
                </div>
              </div>
              <svg
                className={`h-5 w-5 text-gray-400 transition-transform duration-200 ${
                  isExpanded ? 'rotate-180' : ''
                }`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {isExpanded && (
              <div className="px-4 pb-4 space-y-2">
                {product.externalUrls
                  .map((url: string, idx: number) => ({ url, idx }))
                  .filter(({ url }: { url: string }) => url && url.trim() !== '')
                  .map(({ url, idx }: { url: string; idx: number }) => {
                    const siteNames = ["마이리얼트립", "KLOOK", "KKDAY", "GetYourGuide", "트립닷컴"];
                    const siteName = siteNames[idx] || `예약 링크 ${idx + 1}`;

                    return (
                      <a
                        key={idx}
                        href={url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="block p-3 bg-white rounded-xl border border-gray-200 hover:border-blue-300 hover:shadow-sm transition-all duration-200 group/link"
                        onClick={(e) => e.stopPropagation()}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3">
                            <div className="w-6 h-6 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-lg flex items-center justify-center">
                              <span className="text-white text-xs font-bold">{siteName.charAt(0)}</span>
                            </div>
                            <span className="font-medium text-gray-900 group-hover/link:text-blue-600 transition-colors">
                              {siteName}
                            </span>
                          </div>
                          <svg className="h-4 w-4 text-gray-400 group-hover/link:text-blue-500 transition-colors" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                          </svg>
                        </div>
                      </a>
                    );
                  })}
              </div>
            )}
          </div>
        )}
      </div>
    </>
  );
};

export default memo(ProductCard);
