import React, { useEffect, useRef } from 'react';

interface KakaoAdFitProps {
  adUnit?: string;
  width?: number;
  height?: number;
  className?: string;
}

const KakaoAdFit: React.FC<KakaoAdFitProps> = ({ 
  adUnit = "DAN-LiVpm1LeY0Ut7fd9",
  width = 300,
  height = 250,
  className = ""
}) => {
  const adRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // 스크립트가 이미 로드되었는지 확인
    if (window.daum || document.querySelector('script[src*="ba.min.js"]')) {
      return;
    }

    const script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = '//t1.daumcdn.net/kas/static/ba.min.js';
    script.async = true;
    document.head.appendChild(script);

    script.onload = () => {
      // 카카오애드핏 초기화
      if (window.daum && window.daum.adfit) {
        window.daum.adfit.init();
      }
    };

    return () => {
      // cleanup
    };
  }, []);

  useEffect(() => {
    // 광고 단위 초기화
    if (window.daum && window.daum.adfit && adRef.current) {
      window.daum.adfit.display(adRef.current);
    }
  }, [adUnit]);

  return (
    <div className={`kakao-ad-container ${className}`}>
      <ins
        ref={adRef}
        className="kakao_ad_area"
        style={{ display: 'none' }}
        data-ad-unit={adUnit}
        data-ad-width={width}
        data-ad-height={height}
      />
    </div>
  );
};

// TypeScript를 위한 window 확장
declare global {
  interface Window {
    daum?: {
      adfit?: {
        init: () => void;
        display: (element: HTMLElement) => void;
      };
    };
  }
}

export default KakaoAdFit;
