import React, { useState, useMemo } from "react";
import { products as mockProducts } from "../mock/products";
import ProductCard from "../components/ProductCard";
import SearchBar from "../components/SearchBar";
import FilterBar from "../components/FilterBar";
import SortSelector from "../components/SortSelector";
import PartnerWidget from "../components/PartnerWidget";
import Footer from "../components/Footer";

// Google Analytics Ïù¥Î≤§Ìä∏ Ï∂îÏ†Å Ìï®Ïàò
declare global {
  interface Window {
    gtag: (...args: any[]) => void;
  }
}

const trackEvent = (eventName: string, parameters?: any) => {
  if (typeof window !== 'undefined' && window.gtag) {
    window.gtag('event', eventName, parameters);
  }
};

export default function Home() {
  const [keyword, setKeyword] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("Ï†ÑÏ≤¥");
  const [selectedCategory, setSelectedCategory] = useState("Ï†ÑÏ≤¥");
  const [sortBy, setSortBy] = useState("popular");

  // ÌïÑÌÑ∞ÎßÅÎêú ÏÉÅÌíàÎì§ - ÏÑ±Îä• ÏµúÏ†ÅÌôî
  const filteredProducts = useMemo(() => {
    let filtered = mockProducts.filter((product) => {
      // Í≤ÄÏÉâÏñ¥ ÌïÑÌÑ∞
      const searchTarget = [
        product.name,
        product.description,
        ...product.tags,
        ...product.categories,
        ...product.locations,
      ].join(" ").toLowerCase();

      const keywordMatch = keyword === "" || searchTarget.includes(keyword.toLowerCase());

      // ÏßÄÏó≠ ÌïÑÌÑ∞
      const locationMatch = selectedLocation === "Ï†ÑÏ≤¥" || 
        product.locations.some(location => location.includes(selectedLocation));

      // Ïπ¥ÌÖåÍ≥†Î¶¨ ÌïÑÌÑ∞
      const categoryMatch = selectedCategory === "Ï†ÑÏ≤¥" || 
        product.categories.includes(selectedCategory);

      return keywordMatch && locationMatch && categoryMatch;
    });

    // Ï†ïÎ†¨ - ÏÑ±Îä• ÏµúÏ†ÅÌôîÎêú Ï†ïÎ†¨
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "popular":
          return b.views - a.views;
        case "latest":
          // ID ÏàúÏÑúÎ°ú Ï†ïÎ†¨ (ÏµúÏã† Îì±Î°ù Ïàú)
          return parseInt(b.id.split('_')[1]) - parseInt(a.id.split('_')[1]);
        default:
          return 0;
      }
    });

    return filtered;
  }, [keyword, selectedLocation, selectedCategory, sortBy]);

  // Ï∂îÏ≤ú ÏÉÅÌíàÎì§
  const recommendedProducts = useMemo(() => {
    return filteredProducts.filter(product => product.isRecommended);
  }, [filteredProducts]);

  // ÏùºÎ∞ò ÏÉÅÌíàÎì§
  const regularProducts = useMemo(() => {
    return filteredProducts.filter(product => !product.isRecommended);
  }, [filteredProducts]);

  const handleProductClick = (productId: string) => {
    // Google Analytics Ïù¥Î≤§Ìä∏ Ï∂îÏ†Å
    trackEvent('product_click', {
      product_id: productId,
      event_category: 'engagement',
      event_label: 'product_card_click'
    });

    // Ïã§Ï†úÎ°úÎäî ÎùºÏö∞ÌÑ∞Î•º ÏÇ¨Ïö©ÌïòÏó¨ ÏÉÅÌíà ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    console.log(`ÏÉÅÌíà ${productId} ÌÅ¥Î¶≠Îê®`);
    // window.location.href = `/product/${productId}`;
  };

  const handleSearch = (searchTerm: string) => {
    if (searchTerm.trim()) {
      trackEvent('search', {
        search_term: searchTerm,
        event_category: 'search',
        event_label: 'keyword_search'
      });
    }
  };

  const handleFilterChange = (filterType: string, filterValue: string) => {
    trackEvent('filter_change', {
      filter_type: filterType,
      filter_value: filterValue,
      event_category: 'engagement',
      event_label: 'filter_interaction'
    });
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {/* Ìó§Îçî */}
        <div className="text-center mb-8">
          <h1 className="text-3xl md:text-4xl font-bold bg-gradient-to-r from-blue-900 via-blue-800 to-blue-700 bg-clip-text text-transparent mb-3" style={{ fontFamily: 'Poppins, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
            TourStream
          </h1>
          <p className="text-base text-gray-600 font-medium">Ï†ÑÍµ≠ ÏµúÍ≥†Ïùò Ïï°Ìã∞ÎπÑÌã∞Î•º ÌïúÎààÏóê ÎπÑÍµêÌïòÍ≥† ÏòàÏïΩÌïòÏÑ∏Ïöî</p>
        </div>

        {/* Í≤ÄÏÉâÎ∞î */}
        <div className="mb-6">
          <SearchBar 
            value={keyword} 
            onChange={(value) => {
              setKeyword(value);
              handleSearch(value);
            }} 
          />
        </div>

        {/* ÌïÑÌÑ∞ Î∞è Ï†ïÎ†¨ */}
        <div className="mb-6">
          <div className="bg-gray-50 rounded-xl shadow-sm border border-gray-200 p-4">
            <FilterBar
              selectedLocation={selectedLocation}
              selectedCategory={selectedCategory}
              onLocationChange={(location) => {
                setSelectedLocation(location);
                handleFilterChange('location', location);
              }}
              onCategoryChange={(category) => {
                setSelectedCategory(category);
                handleFilterChange('category', category);
              }}
              allProducts={mockProducts}
            />
            <div className="mt-3">
              <SortSelector sortBy={sortBy} onSortChange={setSortBy} />
            </div>
          </div>
        </div>

        {/* Ï∂îÏ≤ú ÏÉÅÌíà ÏÑπÏÖò */}
        {recommendedProducts.length > 0 && (
          <div className="mb-8">
            <div className="flex items-center gap-2 mb-4">
              <div className="w-6 h-6 bg-gradient-to-r from-red-500 to-red-600 rounded-lg flex items-center justify-center">
                <span className="text-white text-xs">üî•</span>
              </div>
              <h2 className="text-xl font-bold text-gray-900">Ï∂îÏ≤ú Ïï°Ìã∞ÎπÑÌã∞</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {recommendedProducts.map((product) => (
                <ProductCard 
                  key={product.id} 
                  product={product} 
                  onProductClick={handleProductClick}
                />
              ))}
            </div>
          </div>
        )}

        {/* ÏùºÎ∞ò ÏÉÅÌíà Î¶¨Ïä§Ìä∏ */}
        <div className="mb-8">
          <div className="flex items-center gap-2 mb-4">
            <div className="w-6 h-6 bg-gradient-to-r from-blue-600 to-blue-700 rounded-lg flex items-center justify-center">
              <span className="text-white text-xs">‚ú®</span>
            </div>
            <h2 className="text-xl font-bold text-gray-900">
              {filteredProducts.length > 0 ? "Î™®Îì† Ïï°Ìã∞ÎπÑÌã∞" : "Í≤ÄÏÉâ Í≤∞Í≥º"}
            </h2>
          </div>
          {regularProducts.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {regularProducts.map((product) => (
                <ProductCard 
                  key={product.id} 
                  product={product} 
                  onProductClick={handleProductClick}
                />
              ))}
            </div>
          ) : (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                <span className="text-gray-500 text-2xl">üîç</span>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§</h3>
              <p className="text-gray-600 mb-4">Îã§Î•∏ ÌÇ§ÏõåÎìúÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî</p>
              <button 
                onClick={() => {
                  setKeyword('');
                  setSelectedLocation('Ï†ÑÏ≤¥');
                  setSelectedCategory('Ï†ÑÏ≤¥');
                }}
                className="px-5 py-2.5 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all duration-200 font-medium shadow-md"
              >
                ÌïÑÌÑ∞ Ï¥àÍ∏∞Ìôî
              </button>
            </div>
          )}
        </div>

        {/* Ï†úÌú¥ÏÇ¨ */}
        <PartnerWidget />
      </div>

      <Footer />
    </div>
  );
}
