import React, { useMemo } from 'react';
import KlookWidget from './KlookWidget';
import TripComWidget from './TripComWidget';
import KKdayWidget from './KKdayWidget';
import AgodaWidget from './AgodaWidget';

interface AdWidgetProps {
  className?: string;
}

const AdWidget: React.FC<AdWidgetProps> = ({ className = "" }) => {
  // 가로형 광고만 (상품 리스트 중간 삽입용)
  const widgets = useMemo(() => [
    {
      id: 'klook1',
      component: <KlookWidget type="deals" aid="65706" className={className} />
    },
    {
      id: 'klook2',
      component: <KlookWidget type="dynamic" adid="1151275" cid="29" className={className} />
    },
    {
      id: 'klook3',
      component: <KlookWidget type="dynamic" adid="1151276" cid="93" className={className} />
    },
    {
      id: 'trip1',
      component: <TripComWidget adId="DB6175843" className={className} />
    },
    {
      id: 'trip2',
      component: <TripComWidget adId="SB6175857" className={className} />
    },
    {
      id: 'kkday',
      component: <KKdayWidget oid="4293" className={className} />
    },
    {
      id: 'agoda',
      component: <AgodaWidget className={className} />
    }
  ], [className]);

  // 랜덤으로 하나 선택
  const selectedWidget = useMemo(() => {
    const randomIndex = Math.floor(Math.random() * widgets.length);
    return widgets[randomIndex].component;
  }, [widgets]);

  return (
    <div className={`ad-widget-wrapper ${className}`}>
      {selectedWidget}
    </div>
  );
};

export default AdWidget;
